'use strict';

var assert = require('assert');
var util = require('util');

exports.mandatory = mandatory;

function mandatory(value, message) {
  assert(value !== undefined, message);
  assert(value !== null, message);
  return value;
};

// Extend base without modifying it.
exports.extend = function extend(base, extra) {
  return util._extend(util._extend({}, base), extra);
};

exports.encodeLibertyPath = function encodeLibertyPath(path) {
  mandatory(path, 'Path should be provided.');
  return encodeURIComponent(path.replace(/\\/g, '/'));
};
